import React, { createContext, useContext, useState, useEffect } from "react";
import axios from "axios";

export const AuthContext = createContext({
  currentUser: null,
  setCurrentUser: () => {},
  token: null,
  setToken: () => {},
});

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [token, setToken] = useState(null);

  useEffect(() => {
    const storedToken = localStorage.getItem("authToken");

    if (storedToken) {
      setToken(storedToken);
      // fetchUserData(storedToken); // Fetch user data based on token here.
    }
  }, []);

  const login = async (email, password) => {
    const url = "http://localhost:8000/api/user/login/";
    const headers = { "X-CSRFTOKEN": getCookie("csrftoken") };
    const formData = new FormData();
    formData.append("username", username);
    formData.append("password", password);

    try {
      const response = await axios.post(url, formData, { headers });

      if (response.data.token && response.data.token.key) {
        setToken(response.data.token.key);
        setCurrentUser(response.data.token.user);
        localStorage.setItem(
          "authToken",
          JSON.stringify({
            key: response.data.token.key,
            expiresAt: response.data.token.expires_at,
          })
        );
      }

      navigate("/");
    } catch (error) {
      if (error.response) {
        setError(error.response.data.error || "Login failed");
      } else {
        setError("Login failed");
      }
      console.error("Axios error: ", error);
    }
  };

  // const fetchUserData = async (authToken) => {
  //   try {
  //     const response = await axios.get("/api/user", {
  //       headers: { Authorization: `Bearer ${authToken}` },
  //     });
  //     setCurrentUser(response.data);
  //   } catch (error) {
  //     console.error("Error fetching user data:", error);
  //     // logout();
  //   }
  // };

  return (
    <AuthContext.Provider
      value={{ currentUser, setCurrentUser, token, setToken }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
