import React from "react";

const AlertComponent = ({ type = "info", title, message, onClose }) => {
  const getAlertClass = () => {
    switch (type) {
      case "success":
        return "alert-success";
      case "error":
        return "alert-danger";
      case "warning":
        return "alert-warning";
      default:
        return "alert-info";
    }
  };

  return (
    <div
      className={`alert ${getAlertClass()} alert-dismissible fade show`}
      role="alert"
    >
      {title && <strong>{title}</strong>}
      {title && message && " "}{" "}
      {/* Add space between title and message if both exist */}
      {message}
      {onClose && (
        <button
          type="button"
          className="btn-close"
          data-bs-dismiss="alert"
          aria-label="Close"
          onClick={onClose}
        ></button>
      )}
    </div>
  );
};

export default AlertComponent;
