// api.js
import { useAuth } from "../contexts/AuthContext"; // Assuming you have an auth module to get the token

const API_BASE_URL = "http://localhost:8000";
let token = null;

export const SetAuthToken = () => {
  token = useAuth().token;
};

class ApiService {
  constructor() {
    this.baseUrl = API_BASE_URL;
    this.token = token;
  }

  async fetchWithAuth(endpoint, options = {}) {
    const headers = {
      Authorization: `Token ${this.token.key}`,
      ...options.headers,
    };

    const response = await fetch(`${this.baseUrl}${endpoint}`, {
      ...options,
      headers,
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.error || `HTTP error! Status: ${response.status}`);
    }

    return response;
  }

  async get(endpoint) {
    return this.fetchWithAuth(endpoint, { method: "GET" });
  }

  async post(endpoint, data) {
    return this.fetchWithAuth(endpoint, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    });
  }

  async delete(endpoint) {
    return this.fetchWithAuth(endpoint, { method: "DELETE" });
  }

  async postFormData(endpoint, formData) {
    return this.fetchWithAuth(endpoint, {
      method: "POST",
      body: formData,
    });
  }

  // Specific API methods
  async fetchSupportedMimetypes() {
    const response = await this.get("/target_conversions/");
    return response.json();
  }

  async convertFile(file, targetMimetype) {
    const formData = new FormData();
    formData.append("original_file", file);
    formData.append("converted_mimetype", targetMimetype);
    return this.postFormData("/", formData);
  }

  async updateUserProfile(userData) {
    return this.put("/api/user/profile", userData);
  }

  async deleteAccount() {
    return this.delete("/api/user/delete_account");
  }
}

export const useApiService = () => {
  const { token } = useAuth();

  // Inject the token getter into the API service
  apiService.getToken = () => token;

  return apiService;
};

export const apiService = new ApiService();
