import React from "react";
import { useState } from "react";

function Alert({ alert }) {
  const [alertNote, setAlertNote] = useState(alert);

  const handleAlertClose = (e) => {
    setAlertNote(null);
  };

  return (
    <div
      className={`alert alert-${alertNote?.type || "danger"} alert-dismissable`}
      role="alert"
    >
      <div className="flex-fill mr-3">
        <p className="mb-0">{alertNote?.message}</p>
      </div>
      <div className="flex-00-auto">
        <i
          className="fa fa-fw fa-times-circle"
          onClick={handleAlertClose}
          role="button"
          aria-label="Close alert message"
          style={{ cursor: "pointer" }}
        ></i>
      </div>
    </div>
  );
}

export default Alert;
