import React from "react";
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import bgImage from "../../assets/images/photo5@2x.jpg";

function ResetPassword() {
  const [email, setEmail] = useState("");
  const [successMessage, setSuccessMessage] = useState(null);
  const [error, setError] = useState(null); // State for error handling

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email) {
      setError("Email or username is required to reset password");
      return;
    }

    const url = "http://localhost:8000/api/user/reset_password/";
    const header = { "X-CSRFTOKEN": getCookie("csrftoken") };
    const formData = new FormData();
    formData.append("email", email);

    try {
      const response = await fetch(url, {
        method: "POST",
        header,
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(errorData.error || "Password reset failed");
      } else {
        console.log("Password reset successful:", response);
        setSuccessMessage(
          "You will receive confirmation email to reset your password"
        );
      }
    } catch (error) {
      setError(error);
      console.error("Fetch error: ", error);
    }
  };

  const getCookie = (name) => {
    let cookieValue = null;
    if (document.cookie && document.cookie !== "") {
      const cookies = document.cookie.split(";");
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        // Does this cookie string begin with the name we want?
        if (cookie.substring(0, name.length + 1) === name + "=") {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  };

  const handleErrorClose = (e) => {
    setError(null);
  };

  const handleSuccessMessageClose = (e) => {
    setSuccessMessage(null);
  };

  return (
    <div id="page-container">
      <main id="main-container">
        <div
          className="bg-image"
          style={{ backgroundImage: `url(${bgImage})` }}
        >
          <div className="row no-gutters bg-gd-sun-op">
            <div className="hero-static col-md-6 d-flex align-items-center bg-white">
              <div className="p-3 w-100">
                <div className="text-center">
                  <a
                    className="link-fx text-warning font-w700 font-size-h1"
                    href="index.html"
                  >
                    <span className="text-dark">File</span>
                    <span className="text-warning">Bee</span>
                  </a>
                  <p className="text-uppercase font-w700 font-size-sm text-muted">
                    Password Reminder
                  </p>
                </div>

                <div className="row no-gutters justify-content-center">
                  <div className="col-sm-8 col-xl-6">
                    <form
                      className="js-validation-reminder"
                      action="be_pages_auth_all.html"
                      method="POST"
                    >
                      {successMessage && (
                        <div
                          className="alert alert-success d-flex align-items-center justify-content-between"
                          role="alert"
                        >
                          <div className="flex-fill mr-3">
                            <p className="mb-0">{successMessage}</p>
                          </div>
                          <div className="flex-00-auto">
                            <i
                              className="fa fa-fw fa-times-circle"
                              onClick={handleSuccessMessageClose}
                              role="button"
                              aria-label="Close error message"
                              style={{ cursor: "pointer" }}
                            ></i>
                          </div>
                        </div>
                      )}
                      {error && (
                        <div
                          className="alert alert-danger d-flex align-items-center justify-content-between"
                          role="alert"
                        >
                          <div className="flex-fill mr-3">
                            <p className="mb-0">{error}</p>
                          </div>
                          <div className="flex-00-auto">
                            <i
                              className="fa fa-fw fa-times-circle"
                              onClick={handleErrorClose}
                              role="button"
                              aria-label="Close error message"
                              style={{ cursor: "pointer" }}
                            ></i>
                          </div>
                        </div>
                      )}
                      <div className="form-group py-3">
                        <input
                          type="text"
                          className="form-control form-control-lg form-control-alt"
                          id="reminder-credential"
                          name="reminder-credential"
                          placeholder="Username or Email"
                          onChange={(e) => setEmail(e.target.value)}
                        />
                      </div>
                      <div className="form-group text-center">
                        <button
                          type="submit"
                          className="btn btn-block btn-hero-lg btn-hero-warning"
                          onClick={handleSubmit}
                        >
                          <i className="fa fa-fw fa-reply mr-1"></i> Reset
                          Password
                        </button>
                        <p className="mt-3 mb-0 d-lg-flex justify-content-lg-between">
                          <Link
                            to="/sign_in"
                            className="btn btn-sm btn-light d-block d-lg-inline-block mb-1"
                          >
                            <i className="fa fa-sign-in-alt text-muted mr-1"></i>
                            Sign In
                          </Link>
                          <Link
                            to="/sign_up"
                            className="btn btn-sm btn-light d-block d-lg-inline-block mb-1"
                          >
                            <i className="fa fa-plus text-muted mr-1"></i>
                            New Account
                          </Link>
                        </p>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
            <div className="hero-static col-md-6 d-none d-md-flex align-items-md-center justify-content-md-center text-md-center">
              <div className="p-3">
                <p className="display-4 font-w700 text-white mb-0">
                  Donâ€™t worry of failure..
                </p>
                <p className="font-size-h1 font-w600 text-white-75 mb-0">
                  ..but learn from it!
                </p>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default ResetPassword;
